import React, { Component } from "react";
import moment from "moment";
import CircularProgress from "@material-ui/core/CircularProgress";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import CardHeader from "@material-ui/core/CardHeader";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import CardMedia from "@material-ui/core/CardMedia";
import { ReactComponent as LikeSvg } from "./image2vector.svg";
import fetchPosts from "./fetchPosts.js"

import "animate.css";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      offset: 0,
      posts: null,
      limit: 3,
      index: 0,         // post index.
      lazyOffset: 5,    // post request threshold.
      classAnim: ""
    };
  }

  handleSetState = () => {
    this.setState({ classAnim : "" })  
  }
  // The state loader is builded as a method that will handle :
  // I   - The lazy loading of the API requests.
  // II  - The cyclcling through API fetched posts.
  // III - The resulting compoponent's state assignation.
  async stateLoader() {
    if (                              // Checking the post queue to evaluate if we need to request more post
      this.state.posts !== null &&      
      this.state.index >= (this.state.posts.length - 1) - this.state.lazyOffset
    ) {
      this.setState({
        offset: this.state.index,
        index: this.state.index + 1
      });
    }
    else if (this.state.posts !== null) {   // Rendering the next post
      this.setState({ index: this.state.index + 1 });
      return;
    }
    this.removeAnim();
    // If no previous conditions have been met, it means that state.post is null.
    // Thus we need to load the posts in the component's state. At this point in any cases,
    // states have been properly updated.
    fetchPosts(this.state);
  }

  addAnim() {
      this.setState({classAnim : "fadeIn"});
  }

  removeAnim() {
      this.setState({classAnim : ""});    
  }

  componentDidMount() {
    this.stateLoader();   // Call upon component mounting.
    setInterval(() => {
         // Timer regulating the call to the stateLoader() method.
      this.addAnim();
      this.stateLoader();
    }, 6000);
  }

  render() {
    const { posts, index } = this.state;

    if (!posts)   // Rendering a spinner while there is no post to consume.
      return (
        <div className="loaderContainer">
          <CircularProgress />
        </div>
      );

    const mediaUrl = posts[index].media.urls.regular;                     // Assignation of the var needed in the custom template
    const avatar = posts[index].media.user.profile_images.medium || "";   // for string interpolation.
    const likeCount = posts[index].media.statistics.likes || "0";
    const personnePersonnes = likeCount < 2 ? "personne" : "personnes";
    const lastName = posts[index].media.user.last_name || "";
    const firstName = posts[index].media.user.first_name || "";
    const title = posts[index].title || "Sans Titre";
    const description = posts[index].description || "Sans déscriptions";
    const timeElapsed = moment(posts[index].media.statistics.created).format("D") || "";
    const jourJours = timeElapsed < 2 ? "jour" : "jours";


    return (
      <div className="App">
        <Card className={"card animated " + this.state.classAnim}>
          <Grid
            container
            spacing={0}
            direction="row"
            style={{ height: "100vh" }}
          >
            <Grid
              item
              lg={7}
              md={7}
              xl={7}
              xs={7}
              className="gridPicWrapper"
              style={{ overflow: "hidden" }}
            >
              <img
                style={{ width: "100%" }}
                title="lorem ipsum"
                src={mediaUrl}
                className="media"
                alt=""
              />
              <CardMedia
                style={{ width: "100%", height: "100%" }}
                title="lorem ipsum"
                image={mediaUrl}
                className="canvaPic"
              />
            </Grid>
            <Grid item lg={5} md={5} xl={5} xs={5} className="grid">
              <CardHeader
                action={
                  <div className="actionWrapper">
                    <Typography className="name">
                      {`${lastName} ${firstName}`}
                    </Typography>
                    <Avatar className="avatar" src={avatar} />
                  </div>
                }
              />
              <CardContent className="vAlign">
                <Typography variant="h4" color="textSecondary">
                  <b>{title}</b>
                </Typography>
                <Typography variant="h6" color="textSecondary" component="p">
                  {description}
                </Typography>
              </CardContent>
              <CardActions disableSpacing className="like">
                <IconButton aria-label="Like the slide">
                  <LikeSvg height="25px" width="25px" />
                </IconButton>
                <Typography>
                  {likeCount} {personnePersonnes}
                </Typography>
              </CardActions>
              <Typography className="date">
                Il y a {timeElapsed} {jourJours}
              </Typography>
            </Grid>
          </Grid>
        </Card>
      </div>
    );
  }
}

export default App;
