import React, { Component } from 'react';
import axios from 'axios';
import moment from 'moment';
import CircularProgress from '@material-ui/core/CircularProgress';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import CardHeader from '@material-ui/core/CardHeader';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import CardMedia from '@material-ui/core/CardMedia';
import { ReactComponent as LikeSvg } from "./image2vector.svg";
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      offset: 0,
      posts: null,
      limit: 3,
      index: 0,
    }
  }

  componentDidMount() {
    const { limit, offset } = this.state;

    axios({
      method: 'get',
      url: `https://api.slstice.com/mock/posts?offset=${offset}&limit=${limit}&api_key=ZkUvF7GBqc0l1Ou6DSPf`,
      headers: {
        'Content-Type': 'application/json',
      }
    }).then(({ data: { response } }) => {
      return Promise.all(response.posts.map(async post => {
        const { data: { response: { media } } } = await axios({
          method: 'get',
          url: `https://api.slstice.com/mock/medias/${post.mediaId}?api_key=ZkUvF7GBqc0l1Ou6DSPf`,
          headers: {
            'Content-Type': 'application/json',
          }
        })
        const { data: { response: { user }} }  = await axios({
          method: 'get',
          url: `https://api.slstice.com/mock/users/${media.owner.username}?api_key=ZkUvF7GBqc0l1Ou6DSPf`,
          headers: {
            'Content-Type': 'application/json',
          }
        })

        media.user = user;
        post.media = media;
        return post;
      }))
    })
    .then((data) => this.setState({ posts: data }))
  }

  //fetchNext(this.state.)

  render () {
    const { posts, index } = this.state    

    if (!posts) return <div className="loaderContainer"><CircularProgress /></div>

    const mediaUrl = posts[index].media.urls.regular;
    const avatar = posts[index].media.user.profile_images.medium || "";
    const likeCount = posts[index].media.statistics.likes || "0";
    const personnePersonnes = (likeCount < 2) ? "personne" : "personnes";
    const lastName = posts[index].media.user.last_name || ""
    const firstName = posts[index].media.user.first_name || "";
    const title = posts[index].media.title || "Sans Titre";
    const description = posts[index].description || "Sans dÃ©scriptions";
    const timeElapsed = posts[index].media.statistics.created || "";
    const jourJours = (timeElapsed < 2) ? "jour" : "jours";

    console.log(posts);

    return (
      <div className="App">
        <Card className="card">
          <Grid
            container
            spacing={0}
            direction="row"
            style={{height :"100vh"}}
          >
            <Grid item lg={7} md={7} xl={7} xs={7} className="gridPicWrapper" style={{overflow: "hidden"}}>
              <img
                style={{width: '100%'}}
                title="lorem ipsum"
                src={mediaUrl}
                className="media"
                alt=""
              />
              <CardMedia
                style={{width: '100%', height: '100%'}}
                title="lorem ipsum"
                image={mediaUrl}
                className="canvaPic"
              />
            </Grid>
            <Grid item lg={5} md={5} xl={5} xs={5} className="grid">
              <CardHeader
              action={
                <div className="actionWrapper">
                  <Typography className="name">
                    {`${lastName} ${firstName}`}
                  </Typography>
                  <Avatar className="avatar" src={avatar} />
                </div>
              } />
              <CardContent className="vAlign">
                <Typography variant="h4" color="textSecondary">
                   <b>{title}</b>
                 </Typography>
                 <Typography variant="h6" color="textSecondary" component="p">
                   {description}
                 </Typography>
              </CardContent>
              <CardActions disableSpacing className="like"> 
                <IconButton aria-label="Like the slide">
                  <LikeSvg height="25px" width="25px"/>
                </IconButton>
                <Typography>
                  {likeCount} {personnePersonnes}
                </Typography>
              </CardActions>
              <Typography className="date">
                Il y a {moment(timeElapsed).format('D')} {jourJours}
              </Typography>
            </Grid>
          </Grid>
        </Card>
      </div>
    );
  }
}


export default App;
