import React, { Component } from 'react';
import axios from 'axios';
import moment from 'moment';
import CircularProgress from '@material-ui/core/CircularProgress';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import CardHeader from '@material-ui/core/CardHeader';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import { ReactComponent as LikeSvg } from "./image2vector.svg";
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      offset: 0,
      posts: null,
      limit: 3,
      index: 0,
      error: null
    }
  }

  componentDidMount() {
    const { limit, offset } = this.state;

    axios({
      method: 'get',
      url: `https://api.slstice.com/mock/posts?offset=${offset}&limit=${limit}&api_key=ZkUvF7GBqc0l1Ou6DSPf`,
      headers: {
        'Content-Type': 'application/json',
      }
    }).then(({ data: { response } }) => {
      return Promise.all(response.posts.map(async post => {
        const { data: { response: { media } } } = await axios({
          method: 'get',
          url: `https://api.slstice.com/mock/medias/${post.mediaId}?api_key=ZkUvF7GBqc0l1Ou6DSPf`,
          headers: {
            'Content-Type': 'application/json',
          }
        })
        const { data: { response: { user }} }  = await axios({
          method: 'get',
          url: `https://api.slstice.com/mock/users/${media.owner.username}?api_key=ZkUvF7GBqc0l1Ou6DSPf`,
          headers: {
            'Content-Type': 'application/json',
          }
        })

        media.user = user;
        post.media = media;
        return post;
      }))
    })
    .then((data) => this.setState({ posts: data }))
  }

  render () {
    const {posts, index, error} = this.state    

    if (!posts) return <div className="loaderContainer"><CircularProgress /></div>

    const lastName = posts[index].media.user.last_name || "";
    const firstName = posts[index].media.user.first_name || "";
    const title = posts[index].media.title || "Untitled";
    const description = posts[index].description || "No description";
    const daysElapsed = moment(posts[index].media.statistics.created).format('D');

    console.log(posts);

    return (
      <div className="App">
        <Grid>
        </Grid>
        <Grid>
        <Card>
          <CardHeader
            action={
              <div className="actionWrapper">
                <Typography color='primary'>
                  {`${lastName} ${firstName}`}
                </Typography>
                <Avatar src={posts[index].media.user.profile_images.small} />
              </div>
            }
          />
           <CardContent>
           <Typography variant="h4" color="textSecondary" component="p">
              {title}
            </Typography>
            <Typography variant="h6" color="textSecondary" component="p">
              {description}
            </Typography>
          </CardContent>
          <CardActions disableSpacing display="flex" flexDirection="row"> 
                <IconButton aria-label="add to favorites">
                  <LikeSvg />
                </IconButton>
                <Typography>
                  {posts[index].media.statistics.likes} personnes
                </Typography>
          </CardActions>
            <Typography>
              Il y a {daysElapsed} jours
            </Typography>
        </Card>
        </Grid>
      </div>
    );
  }
}

export default App;
