import React from 'react';
import axios from 'axios';
import CircularProgress from '@material-ui/core/CircularProgress';


import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      offset: 0,
      posts: null,
      limit: 15,
    }
  }

  componentDidMount() {
    const { limit, offset } = this.state;


    axios({
      method: 'get',
      url: `https://api.slstice.com/mock/posts?offset=${offset}&limit=${limit}&api_key=ZkUvF7GBqc0l1Ou6DSPf`,
      headers: {
        'Content-Type': 'application/json',
      }
    }).then(({ data: { response } }) => {
      return Promise.all(response.posts.map(async post => {
        const { data: { response: { media } } } = await axios({
          method: 'get',
          url: `https://api.slstice.com/mock/medias/${post.mediaId}?api_key=ZkUvF7GBqc0l1Ou6DSPf`,
          headers: {
            'Content-Type': 'application/json',
          }
        })
        const { data }  = await axios({
          method: 'get',
          url: `https://api.slstice.com/mock/users/${media.owner.username}?api_key=ZkUvF7GBqc0l1Ou6DSPf`,
          headers: {
            'Content-Type': 'application/json',
          }
        })


        console.log(data)
        post.media = media;

        return post;
      }))
    })
    .then((data) => console.log(data))
  }

  render () {
    const { posts } = this.state

    console.log(posts);

    return (
      <div className="App">
        {!posts ? <CircularProgress /> :
        <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          JOJO L'ABRICOT
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
        }

      </div>
    );
  }
}

export default App;
