import React, { Component } from 'react';
import axios from 'axios';
import moment from 'moment';
import CircularProgress from '@material-ui/core/CircularProgress';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import CardHeader from '@material-ui/core/CardHeader';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import CardMedia from '@material-ui/core/CardMedia';
import { ReactComponent as LikeSvg } from "./image2vector.svg";
import Animate from 'animate.css-react';
import './App.css';
import 'animate.css/animate.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      offset: 0,
      posts: null,
      limit: 5,
      index: 0,
    }
  }


async stateLoader() {
    if (this.state.posts !== null && this.state.index < this.state.limit - 1) {
      console.log("Posts loaded. No need to fetch more posts for now.");
      this.setState({index : this.state.index + 1});
      console.log("State updated : state.index = " + this.state.index + ". Returning.");
      return;
    }
    else if (this.state.posts !== null && this.state.index === this.state.limit - 1) {
      console.log("No post remaining. Updating state before requesting new posts");
      this.setState({
        offset : this.state.index,
        index : 0
      });
      console.log("State updated : state.offset = " + this.state.offset + ". state.index = " + this.state.index)
    }


    const { limit, offset } = this.state;
    try {
      console.log("Requesting the posts.")
        await axios({
          method: 'get',
          url: `https://api.slstice.com/mock/posts?offset=${offset}&limit=${limit}&api_key=ZkUvF7GBqc0l1Ou6DSPf`,
          headers: {
            'Content-Type': 'application/json',
          }
        })
        .then(async ({ data: { response } }) => {
            const data = await Promise.all(response.posts.map(async (post) => {
            const { data: { response: { media } } } = await axios({
              method: 'get',
              url: `https://api.slstice.com/mock/medias/${post.mediaId}?api_key=ZkUvF7GBqc0l1Ou6DSPf`,
              headers: {
                'Content-Type': 'application/json',
              }
            });
            const { data: { response: { user } } } = await axios({
              method: 'get',
              url: `https://api.slstice.com/mock/users/${media.owner.username}?api_key=ZkUvF7GBqc0l1Ou6DSPf`,
              headers: {
                'Content-Type': 'application/json',
              }
            });
            media.user = user;
            post.media = media;
            return post;
          }));
        return this.setState({ posts: data });
      }) 
    } catch(err){
      setTimeout(()=>this.stateLoader(), 1000);
    }
  }

  componentDidMount() {
    setInterval(()=>{
      this.stateLoader()}
      ,6000);
  }


  render () {
    const { posts, index } = this.state    

    if (!posts) return <div className="loaderContainer"><CircularProgress /></div>

    const mediaUrl = posts[index].media.urls.regular;
    const avatar = posts[index].media.user.profile_images.medium || "";
    const likeCount = posts[index].media.statistics.likes || "0";
    const personnePersonnes = (likeCount < 2) ? "personne" : "personnes";
    const lastName = posts[index].media.user.last_name || ""
    const firstName = posts[index].media.user.first_name || "";
    const title = posts[index].media.title || "Sans Titre";
    const description = posts[index].description || "Sans déscriptions";
    const timeElapsed = posts[index].media.statistics.created || "";
    const jourJours = (timeElapsed < 2) ? "jour" : "jours";

    return (
      <Animate
        appear="fadeInDown"
        durationAppear={1000}
        component="div" >

        <h1>react animate.css</h1>

      </Animate>

    );
  }
}


export default App;
