import React, { Component } from "react";
import axios from "axios";
import moment from "moment";
import CircularProgress from "@material-ui/core/CircularProgress";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import CardHeader from "@material-ui/core/CardHeader";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import CardMedia from "@material-ui/core/CardMedia";
import { ReactComponent as LikeSvg } from "./image2vector.svg";

import 'animate.css';
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    // State initialization
    this.state = { 
      offset: 0,
      posts: null,
      limit: 2,
      index: 0, // post index
    }
  }

// The state loader is builded as a parameterless method that will handle :
// I   - The pooling of the API requests.
// III - The cyclcling through API fetched posts.
// II  - The resulting compoponent"s state assignation.
async stateLoader() {
    //  Evaluating the possibility of post iteration. 
    //  If it's possible, then incrementing the state's post index and returning:
    if (this.state.posts !== null && this.state.index < this.state.limit - 1) {
      this.setState({index : this.state.index + 1});
      return;
    }
    // If it's not possible to iterate, evaluate if there is no more posts to render.
    // If so, assigning the index value to offset in the component's state for the upcoming request,
    // then reset the state's index:
    if (this.state.posts !== null && this.state.index === this.state.limit - 1) {
      this.setState({
        offset : this.state.index,
        index : 0
      });
    }
    // If no previous conditions have been met, it means that state.post is null.
    // Thus we need to load the posts in the component"s state. At this point in any cases, 
    // state have been properly updated.

    const { limit, offset } = this.state;

    // Requesting posts to render via state"s API parameters.
    try {
        axios({
          method: "get",
          url: `https://api.slstice.com/mock/posts?offset=${offset}&limit=${limit}&api_key=ZkUvF7GBqc0l1Ou6DSPf`,
          headers: {
            "Content-Type": "application/json",
          }
        })
        // Mapping the responses to request the /medias/ endpoint,
        // with the propper parameters, for every retrieved post :        
        .then(async ({ data: { response } }) => {
            const postArray = await Promise.all(response.posts.map(async (post) => {
            const { data: { response: { media } } } = await axios({
              method: "get",
              url: `https://api.slstice.com/mock/medias/${post.mediaId}?api_key=ZkUvF7GBqc0l1Ou6DSPf`,
              headers: {
                "Content-Type": "application/json",
              }
            });
            const { data: { response: { user } } } = await axios({
              method: "get",
              url: `https://api.slstice.com/mock/users/${media.owner.username}?api_key=ZkUvF7GBqc0l1Ou6DSPf`,
              headers: {
                "Content-Type": "application/json",
              }
            });
            // Saving freshly fetched media and user object into post"s properties :
            media.user = user;
            post.media = media;
            return post;
          }));
        // Assignation of the newly fetched array of posts to the component"s state :
        //return this.setState({ posts: data });
        return (this.state.posts === null) ? this.setState({posts : postArray}) : this.setState({ posts: [...this.state.posts, ...postArray] });
      }) 
    } catch(err){
      console.log("Connexion problems. Nothing too dramatic.\n" + err)
    }
  }

  // Timer regulating the call to the stateLoader() method
  componentDidMount() {
    // Call upon component mounting
    this.stateLoader();
    setInterval(()=>{
      this.stateLoader()}
      ,6000);
  }


  render () {
    const { posts, index } = this.state    

    // Rendering a spinner while there is no post to consume.
    if (!posts) return <div className="loaderContainer"><CircularProgress /></div>

    // Assignation of the var needed in the custom template
    // for string interpolation
    const mediaUrl = posts[index].media.urls.regular;
    const avatar = posts[index].media.user.profile_images.medium || "";
    const likeCount = posts[index].media.statistics.likes || "0";
    const personnePersonnes = (likeCount < 2) ? "personne" : "personnes";
    const lastName = posts[index].media.user.last_name || ""
    const firstName = posts[index].media.user.first_name || "";
    const title = posts[index].title || "Sans Titre";
    const description = posts[index].description || "Sans déscriptions";
    const timeElapsed = moment(posts[index].media.statistics.created).format("D") || "";
    const jourJours = (timeElapsed < 2) ? "jour" : "jours";

    return (
      <div className="App">
        <Card className="card animated fadeInRight">
          <Grid
            container
            spacing={0}
            direction="row"
            style={{height :"100vh"}}
          >
            <Grid item lg={7} md={7} xl={7} xs={7} className="gridPicWrapper" style={{overflow: "hidden"}}>
              <img
                style={{width: "100%"}}
                title="lorem ipsum"
                src={mediaUrl}
                className="media"
                alt=""
              />
              <CardMedia
                style={{width: "100%", height: "100%"}}
                title="lorem ipsum"
                image={mediaUrl}
                className="canvaPic"
              />
            </Grid>
            <Grid item lg={5} md={5} xl={5} xs={5} className="grid">
              <CardHeader
              action={
                <div className="actionWrapper">
                  <Typography className="name">
                    {`${lastName} ${firstName}`}
                  </Typography>
                  <Avatar className="avatar" src={avatar} />
                </div>
              } />
              <CardContent className="vAlign">
                <Typography variant="h4" color="textSecondary">
                   <b>{title}</b>
                 </Typography>
                 <Typography variant="h6" color="textSecondary" component="p">
                   {description}
                 </Typography>
              </CardContent>
              <CardActions disableSpacing className="like"> 
                <IconButton aria-label="Like the slide">
                  <LikeSvg height="25px" width="25px"/>
                </IconButton>
                <Typography>
                  {likeCount} {personnePersonnes}
                </Typography>
              </CardActions>
              <Typography className="date">
                Il y a {timeElapsed} {jourJours}
              </Typography>
            </Grid>
          </Grid>
        </Card>
      </div>
    );
  }
}


export default App;
