import React from 'react';
import axios from 'axios';
import CircularProgress from '@material-ui/core/CircularProgress';


import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      offset: 0,
      posts: null,
      limit: 15,
    }
  }

  componentDidMount() {
    const { limit, offset } = this.state;


    axios({
      method: 'get',
      url: `https://api.slstice.com/mock/posts?offset=${offset}&limit=${limit}&api_key=ZkUvF7GBqc0l1Ou6DSPf`,
      headers: {
        'Content-Type': 'application/json',
      }
    }).then(({ data: { response } }) => {
      return Promise.all(response.posts.map(async post => {
        const { data: { response: mediaResponse } } = await axios({
          method: 'get',
          url: `https://api.slstice.com/mock/medias/${post.mediaId}?api_key=ZkUvF7GBqc0l1Ou6DSPf`,
          headers: {
            'Content-Type': 'application/json',
          }
        })

        if (mediaResponse.media) {
          const { data: { response: userResponse } }  = await axios({
            method: 'get',
            url: `https://api.slstice.com/mock/users/${response.media.owner.username}?api_key=ZkUvF7GBqc0l1Ou6DSPf`,
            headers: {
              'Content-Type': 'application/json',
            }
          })

          if (userResponse.user) {
            mediaResponse.media.user = userResponse.user;
          }

          post.media = mediaResponse.media;
        }

        return post;
      }))
    })
    .then((data) => this.setState({ posts: data }))
  }

  render () {
    const { posts } = this.state

    console.log(posts);

    return (
      <div className="App">
        {!posts ? <CircularProgress /> :
        <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          JOJO L'ABRICOT
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
        }

      </div>
    );
  }
}

export default App;
