import React, { Component } from 'react';
import axios from 'axios';
import CircularProgress from '@material-ui/core/CircularProgress';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import CardHeader from '@material-ui/core/CardHeader';
import Avatar from '@material-ui/core/Avatar';

import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      offset: 0,
      posts: null,
      limit: 3
    }
  }

  componentDidMount() {
    const { limit, offset } = this.state;

    axios({
      method: 'get',
      url: `https://api.slstice.com/mock/posts?offset=${offset}&limit=${limit}&api_key=ZkUvF7GBqc0l1Ou6DSPf`,
      headers: {
        'Content-Type': 'application/json',
      }
    }).then(({ data: { response } }) => {
      return Promise.all(response.posts.map(async post => {
        const { data: { response: { media } } } = await axios({
          method: 'get',
          url: `https://api.slstice.com/mock/medias/${post.mediaId}?api_key=ZkUvF7GBqc0l1Ou6DSPf`,
          headers: {
            'Content-Type': 'application/json',
          }
        })
        const { data: { response: { user }} }  = await axios({
          method: 'get',
          url: `https://api.slstice.com/mock/users/${media.owner.username}?api_key=ZkUvF7GBqc0l1Ou6DSPf`,
          headers: {
            'Content-Type': 'application/json',
          }
        })

        media.user = user;
        post.media = media;
        return post;
      }))
    })
    .then((data) => this.setState({ posts: data }))
  }

  render () {
    const { posts } = this.state
    if (!posts) return <div className="loaderContainer"><CircularProgress /></div>

    console.log(posts);

    return (
      <div className="App">
        <Card>
          <CardHeader
            action={
              <div className="actionWrapper">
                <Typography color='primary'>
                  {`${posts[1].media.user.last_name || ''} ${posts[1].media.user.first_Name || ''}`}
                </Typography>
                <Avatar src={posts[1].media.user.profile_images.small} />
              </div>
            }
          />
           <CardContent>
           <Typography variant="h1" color="textSecondary" component="p">
              {posts[1].title}
            </Typography>
            <Typography variant="body2" color="textSecondary" component="p">
              {posts[1].description}
            </Typography>
          </CardContent>
        </Card>
      </div>
    );
  }
}

export default App;
